INSERT INTO `questions` (`id`, `question`, `url_title`, `answer`, `asked_by`, `answerd_by`, `date`, `deleted`, `updated`, `updated_user`, `image`, `writer`) VALUES
(1, 'تناول طعام به نسبة عالية من الدهون يسبب مرض سرطان الثدي ؟ ', 'تناول-طعام-به-نسبة-عالية-من-الدهون-يسبب-مرض-سرطان-الثدي-؟-', '<p style=\"text-align: right; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16pt; line-height: 115%; font-family: Tahoma, sans-serif; color: #7f7f7f; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">لم تستطيع العديد من الدراسات أن تثبت وجود علاقة واضحة بين الدهون في الطعام وبين العوامل الخطرة التي تؤدي إلى مرض سرطان الثدي، ومع ذلك فالدراسات مستمرة لمعرفة حقيقة هذا الأمر، إلا أنه يفضل تجنب الدهون للعديد من الأسباب، منها لتقليل نسبة الكولسترول الضار في الجسم وأيضا لترك مساحة لتناول الغذاء الصحي وللحفاظ على وزنك، فزيادة الوزن تعتبر من عوامل الخطورة المسببة لسرطان الثدي حيث أن الدهون تزيد من إنتاج الاستروجين خارج المبايض.</span></p>', 0, 0, '2018-06-14 17:36:00', 0, '2018-07-09 20:47:38', 0, 'question1.png', 0),
(2, 'مرض سرطان الثدي مرض مميت يقتل أي مريضة إذا أصابها ؟', 'مرض-سرطان-الثدي-مرض-مميت-يقتل-أي-مريضة-إذا-أصابها-؟', '<p dir=\"rtl\"><span style=\"font-size: 16pt; line-height: 107%; font-family: Tahoma, sans-serif; color: #7f7f7f; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">يرتفع عدد السيدات اللاتي يٌصبن بمرض سرطان الثدي بشكل ملحوظ سنة تلو الأخرى ولكن معدل الوفاة الناتج عن هذا المرض في تناقص مستمر وذلك ناتج عن توافر إمكانيات أفضل للعلاج والكشف المبكر للأورام من خلال الفحص الذاتي للثدي وأشعة الثدي (الماموجرام).</span></p>', 0, 0, '2018-06-14 17:39:00', 0, '2018-07-09 20:47:38', 0, 'question2.jpg', 0),
(3, 'يحتمل أن تصاب أي سيدة بمرض سرطان الثدي إذا كان لديها أي عامل من عوامل خطورة الإصابة بالمرض ؟ ', 'يحتمل-أن-تصاب-أي-سيدة-بمرض-سرطان-الثدي-إذا-كان-لديها-أي-عامل-من-عوامل-خطورة-الإصابة-بالمرض-؟-', '<p dir=\"rtl\"><span style=\"font-size: 16pt; line-height: 107%; font-family: Tahoma, sans-serif; color: #7f7f7f; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">هناك فعلاً العديد من عوامل الخطورة والتي تجعل النساء أكثر عرضة للإصابة بمرض سرطان الثدي ومع ذلك فليس من الضروري إذا ما كانت السيدة لديها عامل أو أكثر أن تصاب بمرض سرطان الثدي. فهناك بعض السيدات لديهن واحد أو أكثر من هذه العوامل ولم يصبن بهذا المرض وأيضا غالبية السيدات اللاتي أصبن بالمرض لم تكن لديهن أي من هذه العوامل. ولكن معرفة هذه العوامل ربما تساعد السيدة أكثر في الاهتمام بصحة ثديها وعمل كشف دوري حتى يتسنى لها الاستفادة من الاكتشاف المبكر للورم.</span></p>', 0, 0, '2018-06-14 17:45:00', 0, '2018-07-09 20:47:38', 0, 'question3.jpg', 0),
(4, 'مرض سرطان الثدي مرض معدي ؟', 'مرض-سرطان-الثدي-مرض-معدي-؟', '<p dir=\"rtl\"><span style=\"font-size: 18pt; line-height: 150%; font-family: Arial, sans-serif; color: #333333; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">&nbsp;</span><span style=\"font-size: 16pt; line-height: 150%; font-family: Tahoma, sans-serif; color: #7f7f7f; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">مرض سرطان الثدي مرض غير معدي، لا ينتقل من جسم شخص إلى شخص أخر،</span><span style=\"font-size: 16pt; line-height: 150%; font-family: Tahoma, sans-serif; color: #7f7f7f; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">مرض سرطان الثدي عبارة عن انقسامات غير طبيعية لخلايا الجسم</span><span style=\"font-size: 16pt; line-height: 150%; font-family: Tahoma, sans-serif; color: #7f7f7f; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">.</span></p>', 0, 0, '2018-06-14 17:50:00', 0, '2018-07-09 20:47:38', 0, 'question4.jpg', 0),
(5, 'سيدة لديها في العائلة تاريخ مرضي بالإصابة بسرطان الثدي سوف تصاب حتماً بالمرض ؟ \r\n', 'سيدة-لديها-في-العائلة-تاريخ-مرضي-بالإصابة-بسرطان-الثدي-سوف-تصاب-حتماً-بالمرض؟-', '<p style=\"text-align: right; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16pt; line-height: 115%; font-family: Tahoma, sans-serif; color: #7f7f7f; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">على الرغم من أن نسبة الإصابة بسرطان الثدي تزداد عن المعدل إذا كان هناك أحد أفراد العائلة مصاباً بسرطان الثدي مثل الأم ، الأخت، البنت ، أو أفراد العائلة المقربين إلا أن غالبية السيدات اللاتي أصبن بالمرض لم يكن لديهن تاريخ مرضي لكن على السيدة التي كانت أمها أو أختها أو جدتها أن تبدأ بعمل الماموجرام في سن أصغر خمس سنوات من تشخيص أقاربها للمرض</span><span style=\"font-size: 16pt; line-height: 115%; font-family: Tahoma, sans-serif; color: #7f7f7f; background-image: initial; background-position: initial; background-size: initial; background-repeat: initial; background-attachment: initial; background-origin: initial; background-clip: initial;\">.</span></p>', 0, 0, '2018-06-14 17:55:00', 0, '2018-07-09 20:47:38', 0, 'question5.jpg', 0),
(6, 'الماموجرام (أشعة الثدي) ممكن أن يكون سبباً إلى انتشار سرطان الثدي إلى أجزاء أخرى من الجسم ؟', 'الماموجرام-(أشعة-الثدي)-ممكن-أن-يكون-سبباً-إلى-انتشار-سرطان-الثدي-إلى-أجزاء-أخرى-من-الجسم-؟', '<p style=\"text-align: right; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">&nbsp;الماموجرام (أشعة الثدي) هو عبارة عن صورة من أشعة اكس للثدي. الأشعة أو ضغط جهاز الأشعة على الثدي لا يمكن أن يسبب انتشار للسرطان في الجسم وعلى أي سيدة أن تتبع توصيات متخصص الأشعة وتسأله عن أي شيء بخصوص الجهاز</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">.</span></p>', 0, 0, '2018-06-14 18:00:00', 0, '2018-07-09 20:47:38', 0, 'question6.jpg', 0),
(7, 'السيدات ذات الخمسين عام هم فقط اللاتي يكن عُرضه للإصابة بمرض سرطان الثدي ؟', 'السيدات-ذات-الخمسين-عام-هم-فقط-اللاتي-يكن-عُرضه-للإصابة-بمرض-سرطان-الثدي-؟', '<p dir=\"rtl\"><span style=\"font-size: 16.0pt; line-height: 107%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">على الرغم من اكتشاف مرض سرطان الثدي كان في غالبية السيدات من نفس الفئة العمرية إلا أن هناك عدد ملحوظ من الحالات اللاتي أصيبت السيدات في عمر أقل من ثلاثون عاماً وكان المرض في حالة متقدمة وذلك يعود لعدة أسباب منها التأخير في التشخيص وعدم إتباع طرق الكشف المبكر مثل الفحص الذاتي للثدي، وكذلك تأخر النساء المصابات في مراجعة المستشفيات لفترة طويلة حتى بعد اكتشاف كتلة غير طبيعية في الثدي</span><span style=\"font-size: 16.0pt; line-height: 107%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">.</span></p>', 0, 0, '2018-06-14 18:05:00', 0, '2018-07-09 20:47:38', 0, 'question7.jpg', 0),
(8, 'اكتشاف كتلة غير طبيعية في الثدي يعني الإصابة بمرض سرطان الثدي ؟', 'اكتشاف-كتلة-غير-طبيعية-في-الثدي-يعني-الإصابة-بمرض-سرطان-الثدي-؟', '<p style=\"text-align: right; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">&nbsp;إذا إكتشفتي أي تكتل أو تورم أو أي تغير خلال الفحص الذاتي للثدي فإنه من الضروري أن تراجعي الطبيب المختص في الحال، لا تنزعجي فإن معظم هذه التكتلات، الأورام أو التغيرات ليست سرطانية ولكن الطبيب فقط هو الذي يستطع معرفة وتحديد ذلك. أحيانًا النّساء يبتعدن عن الرّعاية الطّبّيّة لأنهن يخفن مما قد يجدنه. تولّ مسئوليّة صحّتك عن طريق الفحص الذاتي للثدي, الزّيارات المنتظمة للطبيب وعمل أشعة الثدي بانتظام</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">.</span></p>', 0, 0, '2018-06-14 18:10:00', 0, '2018-07-09 20:47:38', 0, 'question8.jpg', 0),
(9, 'مرض سرطان الثدي يصيب فقط السيدات وليس الرجال ؟', 'مرض-سرطان-الثدي-يصيب-فقط-السيدات-وليس-الرجال-؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 107%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">أثبتت الإحصائيات في الولايات المتحدة الأمريكية أن النساء معرضات للإصابة أكثر من الرجال فمعدل الإصابة &ndash; رجل لكل 135 حالة امرأة</span><span style=\"font-size: 16.0pt; line-height: 107%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">.</span></p>', 0, 0, '2018-06-14 18:15:00', 0, '2018-07-09 20:47:38', 0, 'question9.jpg', 0),
(10, 'هل الأغذية المصنعة قد تسبب الإصابة بالسرطان؟', 'هل-الأغذية-المصنعة-قد-تسبب-الإصابة-بالسرطان؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">ذكرت أبحاث جديدة قام بها فريق من الباحثين الفرنسيين أن كل زيادة بمقدار 10% من الوجبات الخفيفة المعلبة، والمشروبات الغازية، والسكريات وغيرها من الأغذية المصنعة ، قد يزيد من خطر الإصابة بالسرطان بنسبة 12</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">%.</span></p>\r\n<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">وأوضحت الدراسة أن سرطان الثدي على وجه الخصوص كان مرتبطا باستهلاك أكبر للأغذية المعالجة بنسبة كبيرة</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">.</span></p>', 0, 0, '2018-06-14 18:20:00', 0, '2018-07-09 20:47:38', 0, 'question10.jpg', 0),
(11, 'فقط خسارة القليل من الوزن قد تحمي من سرطان الثدي؟!', 'فقط-خسارة-القليل-من-الوزن-قد-تحمي-من-سرطان-الثدي؟!', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">حيث وجد الباحثون أن إنقاص الوزن بمقدار 5% بعد انقطاع الطمث، يمكن أن يقلل من احتمال الإصابة بسرطان الثدي بنسبة 12%، أي أن 5% للمرأة التي يبلغ وزنها حوالي 170 رطل تحتاج إلى تخسيس الوزن بحوالي 8.5 رطل</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">.</span></p>', 0, 0, '2018-06-14 18:25:00', 0, '2018-07-09 20:47:38', 0, 'question11.jpg', 0),
(12, '    هل جميع كتل الثدي سرطانية؟ ', '----هل-جميع-كتل-الثدي-سرطانية؟-', '<p style=\"text-align: justify; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">&nbsp;معظم كتل الثدي ليست سرطانية، ولكن طبيبك فقط يمكن أن يساعدك في الحصول على الفحوصات التي تحدد ذلك \'بالتأكيد\'. أن الهدف من الممارسة المنتظمة للفحص الذاتي للثدي هو معرفة ما هو \"طبيعي\" بالنسبة لك. أبلغي طبيبك بأية تغيرات فوراً. تذكري أن فرص النجاة عند اكتشاف سرطان الثدي في مراحل متأخرة تقل عن 20 ٪!</span></p>', 0, 0, '2018-06-14 18:30:00', 0, '2018-07-09 20:47:38', 0, 'question12.jpg', 0),
(13, 'هل النظام الغذائي منخفض الدهون يمنع الإصابة بسرطان الثدي؟ ', 'هل-النظام-الغذائي-منخفض-الدهون-يمنع-الإصابة-بسرطان-الثدي؟-', '<p style=\"text-align: right; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">&nbsp; النساء اللواتي يخفضن نسبة الدهون في أجسامهم ويمارسن الرياضة بانتظام أكثر من ثلاث ساعات في الأسبوع، يمكن أن يقللن من خطر الإصابة بسرطان الثدي بنحو 20 ٪. </span></p>\r\n<p style=\"text-align: right; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">وربما يعود ذلك إلى انخفاض دوران استرادأيول [نوع من الاستروجين) مما بدوره يمكن أن يقلل من تنشيط الخلايا التي يحتمل أن تكون في المرحلة ما قبل السرطانية. الوجبات الغذائية قليلة الدهون يمكن أن تساعد المرأة في خفض مجمل الدهون في الجسم، وقد يساعد ذلك بشكل غير مباشر على خفض خطر الإصابة</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">.</span></p>', 0, 0, '2018-06-14 18:35:00', 0, '2018-07-09 20:47:38', 0, 'question13.jpg', 0),
(14, '   أعاني من ألم في الإبط الأيسر - لا إحساس بالألم الخارجي، لكنني أشعر أن هناك شيئاً لا أستطيع تحسسه. هل هذا شيء يستدعي أن أرى طبيباً نسائياً أو طبيب الرعاية الأولية؟ ', '---أعاني-من-ألم-في-الإبط-الأيسر---لا-إحساس-بالألم-الخارجي،-لكنني-أشعر-أن-هناك-شيئاً-لا-أستطيع-تحسسه.-هل-هذا-شيء-يستدعي-أن-أرى-طبيباً-نسائياً-أو-طبيب-الرعاية-الأولية؟-', '<p style=\"text-align: right; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">إن الارتياب خلال الفحص الذاتي للثدي هو أمر مفهوم، ولكن الأهم هو أنك تقومين به. ونظراً لحقيقة أنك وجدت شيئاً لا يشعرك بأن كل شيء على ما يرام تماماً، فإن الأمر يستحق رؤية الطبيب النسائي الخاص بك أو طبيب الرعاية الأولية من أجل فحص سريري جيد للثدي. قد يكون الإحساس بتغير الملمس في منطقة الثدي أو الإبط له تفسير منطقي تماماً، ولكن توجهك للطبيب النسائي بناءً على شعورك بأن هناك شيئاً ما ليس على ما يرام هو بالتأكيد فعل مناسب</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">.</span></p>', 0, 0, '2018-06-14 18:40:00', 0, '2018-07-09 20:47:38', 0, 'question14.jpg', 0),
(15, '  هل صحيح أن الضغط على الثدي خلال الماموجرام يمكن أن ينشر الأورام الصغيرة؟ ', '--هل-صحيح-أن-الضغط-على-الثدي-خلال-الماموجرام-يمكن-أن-ينشر-الأورام-الصغيرة؟-', '<p style=\"text-align: right; line-height: 115%; background: white; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">كلا، لا بد من الضغط للحصول على صورة الأشعة السينية للثدي، كما أن الضغط ليس له تأثير غير عادي على الثدي. قد تحدث بعض الكدمات في بعض الأحيان، إلا أنه لم يتبين أنها تسبب سرطان الثدي أو تساعد على انتشاره</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">. </span></p>', 0, 0, '2018-06-14 18:45:00', 0, '2018-07-09 20:47:38', 0, 'question15.jpg', 0),
(16, 'هل حجم الثدي يتغير فقط في حال الإصابة بالسرطان؟ ', 'هل-حجم-الثدي-يتغير-فقط-في-حال-الإصابة-بالسرطان؟-', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">لا , <span style=\"background: white;\">هنالك عدة عوامل تؤدي لتغير حجم الثدي، منها الحمل أو فترة الحيض</span></span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">.</span></p>', 0, 0, '2018-06-14 18:50:00', 0, '2018-07-09 20:47:38', 0, 'question16.jpg', 0),
(17, 'هل تزيد الإصابة السابقة بسرطان الثدي من احتمالية الإصابة مجددا ؟', 'هل-تزيد-الإصابة-السابقة-بسرطان-الثدي-من-احتمالية-الإصابة-مجددا-؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">من الممكن ان يعود السرطان للظهور بشكل موضعي (في المنطقة التي تمت معالجتها او في منطقة الندوب التي تبقى بعد استئصال الثدي)، او انه قد يعود فيظهر في اماكن اخرى في مختلف انحاء الجسم</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">.</span></p>', 0, 0, '2018-06-14 18:55:00', 0, '2018-07-09 20:47:38', 0, 'question17.jpg', 0),
(18, 'هل يمكن اكتشاف السرطان المبكر فقط بالفحص الذاتي ؟', 'هل-يمكن-اكتشاف-السرطان-المبكر-فقط-بالفحص-الذاتي-؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">هنالك عدة طرق إضافة للفحص الذاتي، للكشف المبكر عن سرطان الثدي منها الميموجرافي، الرنين المغناطيسي</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\"> MRI </span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">والخزعة</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\"> BIOPSY .</span></p>', 0, 0, '2018-06-14 19:00:00', 0, '2018-07-09 20:47:38', 0, 'question18.jpg', 0),
(19, 'وفق فحص التدريج للسرطان كلما زادت الدرجة قلت الخطورة ؟', 'وفق-فحص-التدريج-للسرطان-كلما-زادت-الدرجة-قلت-الخطورة-؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">لا ,<span style=\"background: white;\"> كلما زادت الدرجة زادت الخطورة، وكذلك تعقيدات العلاج وبالتالي الشفاء. ويتم تصنيف الورم السرطاني على جدول من 0 الى اربعة.</span></span></p>', 0, 0, '2018-06-14 19:05:00', 0, '2018-07-09 20:47:38', 0, 'question19.jpg', 0),
(20, 'هل ينتشر سرطان الثدي باتجاه الغدد اللمفاوية تحت الإبط ؟', 'هل-ينتشر-سرطان-الثدي-باتجاه-الغدد-اللمفاوية-تحت-الإبط-؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f;\">لا ,<span style=\"background: white;\"> كلما زادت الدرجة زادت الخطورة، وكذلك تعقيدات العلاج وبالتالي الشفاء. ويتم تصنيف الورم السرطاني على جدول من 0 الى اربعة</span></span></p>', 0, 0, '2018-06-14 19:10:00', 0, '2018-07-09 20:47:38', 0, 'question20.png', 0),
(21, 'هل تتبع العمليات الجراحية لاستئصال الورم السرطاني علاجات بالاشعاعات ؟', 'هل-تتبع-العمليات-الجراحية-لاستئصال-الورم-السرطاني-علاجات-بالاشعاعات-؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">العمليات الجراحية لاستئصال الورم السرطاني تتبعها، دائما، علاجات بالاشعاعات، وذلك من اجل تدمير اية خلايا سرطانية يمكن ان تكون قد بقيت في المكان</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">.</span></p>', 0, 0, '2018-06-14 19:15:00', 0, '2018-07-09 20:47:38', 0, 'question21.jpg', 0),
(22, 'من الخطوات التي تقلل مخاطر الإصابة بسرطان الثدي: تجنب التعرض لمبيدات حشرية ؟', 'من-الخطوات-التي-تقلل-مخاطر-الإصابة-بسرطان-الثدي:-تجنب-التعرض-لمبيدات-حشرية-؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">بالاضافة الى تجنب التعرض لمبيدات حشرية, كذلك اتخاذ بعض الخطوات لتغيير نمط حياتك يمكن ان يكون عاملا مؤثرا في تقليل مخاطر اصابتك بمرض سرطان الثدي</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">.</span></p>', 0, 0, '2018-06-14 19:20:00', 0, '2018-07-09 20:47:38', 0, 'question22.jpg', 0),
(23, 'هل معظم الكتل التي يتم اكتشافها بالثدي ليست خبيثة ؟', 'هل-معظم-الكتل-التي-يتم-اكتشافها-بالثدي-ليست-خبيثة-؟', '<p style=\"text-align: right; line-height: 115%; direction: rtl; unicode-bidi: embed;\"><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">ومع ذلك، فان العلامة المبكره الاكثر شيوعا لمرض سرطان الثدي لدى النساء والرجال على حد سواء، هي ظهور كتلة او تكثـف في نسيج الثدي. هذه الكتلة غير مؤلمة, غالبا</span><span style=\"font-size: 16.0pt; line-height: 115%; font-family: \'Tahoma\',sans-serif; color: #7f7f7f; background: white;\">.</span></p>', 0, 0, '2018-06-14 19:00:00', 0, '2018-07-09 20:47:38', 0, 'question23.jpg', 0);
