INSERT INTO `terminologies` (`term`, `definition`, `id`, `deleted`, `updated`, `updated_user`, `writer`) VALUES
('مرحلة متقدمة من السرطان (Advanced Cancer)', '<p><span style=\"font-size: 16.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; color: black;\">لقد انتشر السرطان في أماكن أخرى بالجسم وعادة لا يمكن علاجه أو السيطرة عليه بالعلاج.</span></p>', 1, 0, '2018-07-09 20:49:59', 0, 0),
('مثبط أروماتايز ((Aromatase Inhibitor', '<p><span style=\"font-size: 16.0pt; line-height: 107%; font-family: \'Times New Roman\',serif; color: black;\">عقار يمنع تكوين هرمون الإستراديول، هرمون الإناث، وذلك من خلال التدخل في إفراز إنزيم الأورماتاز. وتستخدم مثبطات الأورماتاز كنوع من العلاج الهرموني بالنسبة للسيدات اللاتي انقطع عنهن الطمث والمصابات بسرطان الثدي المعتمد في إصابته على الهرمونات. </span></p>', 2, 0, '2018-07-09 20:49:59', 0, 0),
('الإبط (Axilla)', '<p>المنطقة الموجودة تحت الذراع.</p>', 3, 0, '2018-07-09 20:49:59', 0, 0),
('تجريف العقد اللمفية الإبطية (Axillary Dissection)', '<p dir=\"rtl\">يستأصل الجراح العقد اللمفية الموجودة في منطقة تحت الإبط. ويطلق عليها أيضًّا تجريف العقد اللمفية الإبطية.</p>', 4, 0, '2018-07-09 20:49:59', 0, 0),
('العقد اللمفية الإبطية (Axillary Lymph Nodes)', '<p dir=\"rtl\">هي عقد لمفية موجودة في منطقة تحت الإبط التي تسحب العقد اللمفية من الثدي والمناطق القريبة منه.</p>', 5, 0, '2018-07-09 20:49:59', 0, 0),
('ورم حميد (Benign)', '<p dir=\"rtl\">لا يُعد من الأورام السرطانية. وقد ينمو الورم الحميد ليصير أكبر ولكنه لا ينتشر ليمتد إلى أجزاء أخرى من الجسم.</p>', 6, 0, '2018-07-09 20:49:59', 0, 0),
('فحص العينات (Biopsy)', '<p dir=\"rtl\">أخذ عينة من الخلايا أو الأنسجة ليفحصها إخصائي علم الأمراض. قد يفحص إخصائي علم الأمراض النسيج تحت الميكروسكوب أو إجراء فحوصات أخرى على الخلايا أو الأنسجة.</p>', 7, 0, '2018-07-09 20:49:59', 0, 0),
('الأوعية الدموية (Blood Vessel)', '<p dir=\"rtl\">عبارة عن أنبوب من خلاله يسري الدم في الجسم. وتحتوي الأوعية الدموية على شبكة من الشرايين والشعيرات الدموية والشعيرات والأوردة والعروق.</p>', 8, 0, '2018-07-09 20:49:59', 0, 0),
('المعالجة الإشعاعية الداخلية (Brachytherapy)', '<p dir=\"rtl\">نوع من العلاج الإشعاعي تمر فيه المادة الإشعاعية من خلال إبرة أو بذرة أو أسلاك أو أنبوب قسطرة مباشرة إلى الورم أو بالقرب منه. ويطلق عليه أيضًا المعالجة الإشعاعية الداخلية والعلاج الإشعاعي المزروع.</p>', 9, 0, '2018-07-09 20:49:59', 0, 0),
('الجراحة المحافظة على الثدي (Breast-Sparing Surgery) ', '<p dir=\"rtl\">عملية جراحية لاستئصال سرطان الثدي ولكن دون استئصال الثدي. وتشتمل أنواع الجراحات المحافظة على الثدي على استئصال الكتلة الورمية (استئصال العقدة اللمفية)، واستئصال جزء من الثدي (استئصال ربع الثدي) أو الاستئصال الثديي القطعي (استئصال السرطان وكذلك جزء من أنسجة الثدي الموجودة حول الورم أو المبطنة لعضلات الصدر الموجودة أسفل الورم). ويطلق عليها أيضًا جراحة للحفاظ على الثدي.</p>', 10, 0, '2018-07-09 20:49:59', 0, 0),
('السرطان (Cancer)', '<p dir=\"rtl\">مصطلح مستخدم للإشارة إلى انقسام غير طبيعي للخلايا دون الخضوع للسيطرة ومن الممكن أن تغزو الأنسجة القريبة من الورم. وقد تنتشر الخلايا السرطانية أيضًا لتصل إلى أجزاء أخرى بالجسم من خلال الدم والجهاز اللمفي.</p>', 11, 0, '2018-07-09 20:49:59', 0, 0),
('سرطان (Carcinoma in Situ)', '<p dir=\"rtl\">مجموعة من الخلايا غير السليمة التي تبقى في أول مكان تكونت به، ولم تنتشر هذه الخلايا، وربما تصير هذه الخلايا غير السليمة سرطانًا وتنتشر في الأنسجة السليمة القريبة. وتُسمى هذه المرحلة: المرحلة الصفرية.</p>', 12, 0, '2018-07-09 20:49:59', 0, 0),
('خلية (Cell)', '<p dir=\"rtl\">وحدة مستقلة تكون أنسجة الجسم. وجميع الكائنات الحية تتكون من خلية واحدة أو أكثر.</p>', 13, 0, '2018-07-09 20:49:59', 0, 0),
('العلاج الكيميائي (Chemotherapy)', '<p dir=\"rtl\">العلاج باستخدام العقاقير التي تدمر الخلايا السرطانية.</p>', 14, 0, '2018-07-09 20:49:59', 0, 0),
('التجربة السريرية (Clinical Trial)', '<p dir=\"rtl\">نوع من الأبحاث التي تفحص مدى نجاح الطرق الطبية المستخدمة لعلاج المرضى. وهذه الدراسات تفحص المناهج الجديدة الخاصة بالفحص أو الوقاية أو التشخيص أو علاج المرض. ويطلق عليها أيضًا الدراسات السريرية.</p>', 15, 0, '2018-07-09 20:49:59', 0, 0),
('المادة الصبغية (Contrast material)', '<p dir=\"rtl\">عبارة عن مادة صبغية أو غيرها من المواد التي تساعد على إظهار المناطق غير السليمة بداخل الجسم، ويتم إعطاؤها للمريض من خلال الحقن في الوريد أو من خلال حقنة شرجية أو عن طريق الفم. وقد تُستخدم مادة التباين في الأشعة السينية والأشعة المقطعية بالكمبيوتر أو التصوير بالرنين المغناطيسي أو غيرها من فحوصات التصوير والأشعة.</p>', 16, 0, '2018-07-09 20:49:59', 0, 0),
('الأشعة المقطعية بالكمبيوتر (CT Scan)', '<p dir=\"rtl\">مجموعة من الصور لمناطق داخل الجسم مأخوذة من زوايا مختلفة. هذه الصور يكونها الكمبيوتر المتصل بجهاز الأشعة السينية. ويطلق عليها أيضًا تصوير شعاعي طبقي بالكمبيوتر.</p>', 17, 0, '2018-07-09 20:49:59', 0, 0),
('قناة (Ducts)', '<p dir=\"rtl\">في الطب، هي قناة أو وعاء في الجسم تتدفق من خلاله السوائل.</p>', 18, 0, '2018-07-09 20:49:59', 0, 0),
('السرطان القنوي للغدد الثديية (Ductal carcinoma)', '<p dir=\"rtl\">أكثر أنواع سرطان الثدي شيوعًا. ويبدأ في الخلايا المبطنة للقنوات الناقلة للبن الموجودة في الثدي.</p>', 19, 0, '2018-07-09 20:49:59', 0, 0),
('سرطان بالغدد الثديية (Ductal carcinoma in Situ)', '<p dir=\"rtl\"><span style=\"font-size: 12pt;\">حيث نجد الخلايا غير السليمة بالقرب من النسيج المبطن للقنوات الناقلة للبن. وفي هذه الحالة لا تنتشر الخلايا غير السليمة خارج القنوات لتصل إلى أنسجة أخرى بالثدي. وفي بعض الحالات، قد يصير السرطان بالغدد الثديية وينتشر ليصل إلى أنسجة أخرى، رغم أنه لا يمكن في الوقت الحالي توقع أي من الآفات السرطانية يمكن أن تصير. </span></p>\r\n<p dir=\"rtl\"><span style=\"font-size: 12pt;\">ويطلق عليه أيضًا سرطان داخل القنوات الناقلة للبن. </span></p>', 20, 0, '2018-07-09 20:49:59', 0, 0),
('مرحلة مبكرة من سرطان الثدي (Early-stage breast cancer)', '<p dir=\"rtl\">هو سرطان الثدي الذي لم ينتشر ويتعد منطقة الثدي أو العقد اللمفية الإبطية. وهذا يشتمل على السرطان بالغدد الثديية وسرطان الثدي في المرحلة الأولى والمرحلة الثانية (أ) والمرحلة الثانية (ب) والمرحلة الثالثة (أ).</p>', 21, 0, '2018-07-09 20:49:59', 0, 0),
('الإستروجين (Estrogen)', '<p dir=\"rtl\">نوع من الهرمونات التي ينتجها الجسم يساعد على تنمية خصائص الجنس لدى الإناث والحفاظ عليها ونمو العظام. ومن الممكن أن يتم إنتاج هرمون الإستروجين في المعمل. وقد يستخدم الهرمون كوسيلة لتحديد النسل وعلاج أعراض انقطاع الطمث واضطرابات الدورة الشهرية وتخلخل العظام وغيرها من الحالات الصحية.</p>', 22, 0, '2018-07-09 20:49:59', 0, 0),
('المعالجة الإشعاعية الخارجية (External Radiation Therapy)', '<p dir=\"rtl\">نوع من العلاج الإشعاعي يستخدم فيه جهاز يهدف إلى تسليط أشعة ذات طاقة عالية على الخلايا السرطانية من خارج الجسم. ويطلق عليها أيضًا العلاج الإشعاعي بالأشعة الخارجية.</p>', 23, 0, '2018-07-09 20:49:59', 0, 0),
('ليفي (Fibrous)', '<p dir=\"rtl\">يحتوي على ألياف أو يشبه الألياف.</p>', 24, 0, '2018-07-09 20:49:59', 0, 0),
('غدة (Gland)', '<p dir=\"rtl\">&nbsp;عضو يفرز مادة واحدة أو أكثر مثل الهرمونات أو العصارات الهاضمة أو العرق أو الدموع أو اللعاب أو اللبن.</p>', 25, 0, '2018-07-09 20:49:59', 0, 0),
('بروتين HER2 (HER2)', '<p dir=\"rtl\">&nbsp;عبارة عن بروتين يشترك في نمو الخلايا السليمة. ويوجد هذا الهرمون في بعض أنواع الخلايا.</p>', 26, 0, '2018-07-09 20:49:59', 0, 0),
('مستقبلات هرمونية (Hormone Therapy)', '<p dir=\"rtl\">بروتين الخلية الذي يرتبط ببروتين معين. وقد تكون المستقبلات الهرمونية موجودة على سطح الخلية أو بداخلها. وتحدث العديد من التغيرات في الخلية بعد ارتباطها بالمستقبل الخاص بها.</p>', 27, 0, '2018-07-09 20:49:59', 0, 0),
('العلاج الهرموني (Hormone Therapy)', '<p dir=\"rtl\">عبارة عن طريقة معالجة تتم من خلال إضافة الهرمونات أو تعطيلها أو إزالتها. وبالنسبة لحالات معينة (في حالة الإصابة بالسكري أو انقطاع الطمث مثلاً)، يتم إعطاء الهرمونات لضبط المستويات المنخفضة للهرمونات. ومن أجل إبطاء أو وقف نمو سرطانات معينة (مثل سرطان الثدي أو البروستاتا)، قد يتلقى المريض هرمونات مُخلقة أو عقاقير أخرى لتعطيل الهرمونات الطبيعية بالجسم. وفي بعض الأحيان، يكون هناك حاجة إلى إجراء عملية جراحية لاستئصال الغدة التي تنتج هرمونًا معينًا. ويطلق عليه أيضًا المعالجة الهرمونية: Endocrine therapy وHormonal Therapy وHormone treatment.</p>', 28, 0, '2018-07-09 20:49:59', 0, 0),
('سرطان ثدي التهابي (Inflammatory Breast Cancer)', '<p dir=\"rtl\">أحد أنواع سرطان الثدي, ويبدو فيه الثدي محمرًّا ومتورمًا ومرتفع الحرارة. وقد يبدو جلد الثدي مثقبًا أشبه بقشرة البرتقال (Peau d\'orange).</p>\r\n<p dir=\"rtl\">ويحدث الاحمرار ودرجة الحرارة المرتفعة لأن الخلايا السرطانية تسد الأوعية اللمفية الموجودة في الجلد.</p>', 29, 0, '2018-07-09 20:49:59', 0, 0),
('داخل الوريد (Intravenous)', '<p dir=\"rtl\">عادة ما يستخدم هذا المصطلح للإشارة إلى الطريقة التي يتم بها إعطاء العقار أو مواد أخرى من خلال إبرة أو أنبوب يتم إدخاله إلى الوريد. ويشار إليه اختصارًا بـ IV.</p>', 30, 0, '2018-07-09 20:49:59', 0, 0),
('سرطان الدم (Leukemia)', '<p dir=\"rtl\">سرطان يبدأ في الأنسجة المكونة للدم مثل نخاع العظام ويتسبب هذا النوع من السرطان في إنتاج عدد كبير من خلايا الدم وإدخالها إلى مجرى الدم.</p>', 31, 0, '2018-07-09 20:49:59', 0, 0),
('الهرمون (LH-RH agonist)', '<p dir=\"rtl\">عقار يُستخدم لتثبيط إفراز الهرمونات الجنسية. وفي الرجال، يتسبب الهرمونفي انخفاض نسبة هرمون التستوستيرون. وفي السيدات، يتسبب الهرمون في انخفاض هرمون الإستروجين وغيره من الهرمونات الجنسية.</p>\r\n<p dir=\"rtl\">وهو مختصر Luteinizing Hormone-releasing hormone agonist.</p>', 32, 0, '2018-07-09 20:49:59', 0, 0),
('الفص (Lobe)', '<p dir=\"rtl\">جزء من عضو مثل الكبد أو الرئة أو الثدي أو الغدة الدرقية أو المخ.</p>', 33, 0, '2018-07-09 20:49:59', 0, 0),
('السرطان الفصيصي (Lobular Carinoma)', '<p dir=\"rtl\">يبدأ هذا النوع من السرطان من الفصيصات (الغدد التي تفرز اللبن) الموجودة في الثدي. والسرطان الفصيصي اللابد (LCIS) هو عبارة عن خلايا غير سليمة موجودة في الفصيصات فقط.</p>\r\n<p dir=\"rtl\">وعندما ينتشر السرطان من الفصيصات ليصل إلى الأنسجة المحيطة، فإنه يُسمى السرطان الفصيصي. وعادةً لا يصير السرطان الفصيصي ، ولكن إصابة أحد الثديين بالسرطان الفصيصي تزيد من خطر الإصابة بالسرطان في أحد الثديين.</p>', 34, 0, '2018-07-09 20:49:59', 0, 0),
('الفصيصات (Lobule)', '<p dir=\"rtl\">عبارة عن فص صغير أو جزء صغير جدًّا من الفص.</p>', 35, 0, '2018-07-09 20:49:59', 0, 0),
('جراحة استئصالية للورم (Lumpectomy)', '<p dir=\"rtl\">&nbsp;عبارة عن عملية جراحية لاستئصال الأنسجة غير السليمة أو استئصال السرطان من الثدي وجزء صغير من الأنسجة السليمة القريبة. وهي نوع من أنواع الجراحة المحافظة على الثدي.</p>', 36, 0, '2018-07-09 20:49:59', 0, 0),
('العقد اللمفية (Lymph Node)', '<p dir=\"rtl\">عبارة عن كتلة مكورة من النسيج اللمفي تحيط بغشاء الأنسجة الضامة. وترشح العقد اللمفية السائل اللمفي وتقوم بتخزين خلايا الدم البيضاء، وتوجد أيضًا في الأوعية اللمفية، كما أنه يُطلق عليها الغدة اللمفية.</p>', 37, 0, '2018-07-09 20:49:59', 0, 0),
('الوعاء اللمفي (Lymph Vessel)', '<p dir=\"rtl\">عبارة عن أنبوب رفيع يحمل السائل اللمفي وخلايا الدم البيضاء عبر الجهاز اللمفي. ويطلق عليه الأوعية اللمفية (Lymphatic Vessel).</p>', 38, 0, '2018-07-09 20:49:59', 0, 0),
('وذمة لمفية (Lymphedema)', '<p dir=\"rtl\">&nbsp;حالة يتجمع فيها السائل الفائض في الأنسجة ويتسبب في التورم. وقد تحدث الوذمة اللمفية في الذراع أو القدم بعد استئصال الأوعية اللمفية أو العقد اللمفية الموجودة تحت الإبط أو عند الأربية أو معالجتها بالإشعاع.</p>', 39, 0, '2018-07-09 20:49:59', 0, 0),
('ورم خبيث (Malignant)', '<p dir=\"rtl\">&nbsp;عبارة عن ورم سرطاني. وقد تغزو الأورام السرطانية الأنسجة القريبة وتدمرها وتنتشر في أجزاء أخرى من الجسم.</p>', 40, 0, '2018-07-09 20:49:59', 0, 0),
('صورة الثدي الإشعاعية (Mammogram)', '<p dir=\"rtl\">عبارة عن أشعة سينية على الثدي.</p>', 41, 0, '2018-07-09 20:49:59', 0, 0),
('اخصائي أورام (Medical Oncologist)', '<p dir=\"rtl\">طبيب متخصص في تشخيص مرض السرطان ومعالجته باستخدام العلاج الكيميائي، والهرموني، الموجه والبيولوجي. وإخصائي الأورام هو المقدم الرئيسي للرعاية الصحية لمريض السرطان، ويقدم أيضًا الدعم، ويمكنه تنسيق العلاج الذي يقدمه الأخصائيون الآخرون.</p>', 42, 0, '2018-07-09 20:49:59', 0, 0),
('انقطاع الطمث/سن اليأس (Menopause)', '<p dir=\"rtl\">فترة يتوقف فيها مبيض المرأة عن العمل وتنقطع الدورة الشهرية عنها. وعادةً ما ينقطع الطمث في سن الخمسين تقريبًا. ويُقال إن المرأة انقطع عنها الطمث إذا توقفت الدورة الشهرية لمدة 12 شهرًا متعاقبًا. وتشتمل أعراض انقطاع الطمث على ارتفاع درجة حرارة الجزء العلوي من الجسم والتغيرات المزاجية والتعرق الليلي ومشاكل التركيز والعقم.</p>', 43, 0, '2018-07-09 20:49:59', 0, 0),
('الدورة الشهرية (Menstrual Period)', '<p dir=\"rtl\">عبارة عن تفريغ دوري للدم والأنسجة الموجودة في الرحم. ومن سن البلوغ وحتى سن اليأس، تحدث الدورة الشهرية كل 28 يومًا تقريبًا ولكن الدورة الشهرية تنقطع في أثناء الحمل.</p>', 44, 0, '2018-07-09 20:49:59', 0, 0),
('نقيلي (Metastatic)', '<p dir=\"rtl\">تعني انتشار السرطان من جزء إلى آخر في الجسم.</p>', 45, 0, '2018-07-09 20:49:59', 0, 0),
('استئصال الثدي الجذري المعدل (Modified Radical Mastectomy)', '<p dir=\"rtl\">عملية جراحية لسرطان الثدي يتم فيها استئصال الثدي ومعظم الغدد اللمفية الموجودة تحت الإبط أو كلها والأنسجة المبطنة فوق عضلات الصدر. وفي بعض الأحيان، يستأصل الجراح جزءًا من عضلات جدار القص.</p>', 46, 0, '2018-07-09 20:49:59', 0, 0),
('التصوير بالرنين المغناطيسي (MRI)', '<p dir=\"rtl\">إجراء يتم فيه استخدام موجات الراديو ومغناطيس قوي متصل بجهاز كمبيوتر من أجل أخذ صور تفصيلية لمناطق داخل الجسد. وهذه الصور من الممكن أن تظهر الاختلاف بين الأنسجة السليمة والأنسجة المريضة. والتصوير بالرنين المغناطيسي يصدر صورًا للأعضاء والأنسجة الرخوة أفضل من تلك التي تصدرها تقنيات التصوير الأخرى مثل الأشعة المقطعية بالكمبيوتر أو الأشعة السينية. والتصوير بالرنين المغناطيسي مفيد على نحو خاص لتصوير المخ والعمود الفقري والأنسجة الرخوة للمفاصل والجزء الداخلي للعظام. ويعرف أيضًا بـ Magnetic Resonance Imaging.</p>', 47, 0, '2018-07-09 20:49:59', 0, 0),
('ممرضة أورام (Oncology Nurse)', '<p dir=\"rtl\">هي ممرضة متخصصة في معالجة مرضى السرطان ورعايتهم.</p>', 48, 0, '2018-07-09 20:49:59', 0, 0),
('عضو (Organ)', '<p dir=\"rtl\">جزء من الجسم يؤدي وظيفة معينة. على سبيل المثال، القلب عضو.</p>. ', 49, 0, '2018-07-09 20:49:59', 0, 0),
('المبيض (Ovary)', '<p dir=\"rtl\">هو أحد المبيضين المرتبطين بالغدد التناسلية الأنثوية التي تتكون فيها البويضات. والمبيضان موجودان في الحوض، على جانبي الرحم.</p>', 50, 0, '2018-07-09 20:49:59', 0, 0),
('الاستئصال الجزئي للثدي (Partial mastectomy)', '<p dir=\"rtl\">&nbsp;استئصال الورم السرطاني وبعض أنسجة الثدي الموجودة حول الورم أو الأنسجة المبطنة لعضلات الصدر أسفل الورم. وعادة ما يتم استئصال بعض العقد اللمفية الموجودة تحت الإبط. ويطلق عليه أيضًا الاستئصال الثديي القطعي Segmental Mastectomy.</p>', 51, 0, '2018-07-09 20:49:59', 0, 0),
('تصوير مقطعي بالإصدار البوزيتروني (PET scan)', '<p dir=\"rtl\">إجراء يتم فيه حقن كمية صغيرة من الجلوكوز المشع (السكر) في الوريد، ويتم استخدام ماسح لأخذ صور تفصيلية ومحوسبة لمناطق داخل الجسم والمستخدم في تصويرها الجلوكوز. ونظرًا لأن الخلايا السرطانية تستهلك المزيد من الجلوكوز أكثر من الكمية التي تستخدمها الخلايا السليمة، فمن الممكن استخدام الصور للبحث عن خلايا سرطانية في الجسم. ويطلق عليه أيضًا Positron Emission Tomography Scan.</p>', 52, 0, '2018-07-09 20:49:59', 0, 0),
('أخصائي علاج طبيعي (Physical Therapist)', '<p dir=\"rtl\">&nbsp;خبير رعاية صحية يقدم تمارين رياضية وأنشطة بدنية تساعد على تقوية العضلات واستعادة لياقتها وحركتها الطبيعية.</p>', 53, 0, '2018-07-09 20:49:59', 0, 0),
('جرّاح تجميل (Plastic Surgeon)', '<p dir=\"rtl\">جراح متخصص في تقليل الندب أو التشويهات التي قد تحدث نتيجة لوقوع الحوادث والتشويهات الخلقية أو معالجة الأمراض.</p>', 54, 0, '2018-07-09 20:49:59', 0, 0),
('بروجسترون (Progesterone)', '<p dir=\"rtl\">نوع من الهرمونات التي ينتجها الجسم والتي تلعب دورًا في الدورة الشهرية وحدوث الحمل. ومن الممكن تصنيع البروجسترون في المعمل. وقد يُستخدم كوسيلة لتحديد النسل ومعالجة الاضطرابات في الدورة الشهرية والعقم وأعراض انقطاع الطمث وغيرها من الحالات المرضية.</p>', 55, 0, '2018-07-09 20:49:59', 0, 0),
('الإشعاع (Radiation)', '<p dir=\"rtl\">طاقة يتم إطلاقها على هيئة موجات ازدواجية أو موجات كهرومغناطيسية. ومن المصادر الشائعة للإشعاع غاز الرادون والأشعة الكونية من الفضاء الخارجي والأشعة السينية الطبية والطاقة المستخلصة من النظائر المشعة (حالة غير مستقرة لعنصر الكيميائي يطلق خلالها إشعاعًا في أثناء تكسره ليصير أكثر استقرارًا).</p>', 56, 0, '2018-07-09 20:49:59', 0, 0),
('أخصائي علاج الأورام بالإشعاع (Radiation Oncologist)', '<p dir=\"rtl\">طبيب متخصص في استخدام الإشعاع لعلاج السرطان.</p> ', 57, 0, '2018-07-09 20:49:59', 0, 0),
('العلاج الإشعاعي (Radiation Therapy)', '<p dir=\"rtl\">استخدام إشعاع ذي طاقة عالية من الأشعة السينية وأشعة جاما والنيوترونات والبروتونات ومصادر أخرى لتدمير الخلايا السرطانية وتقليص الأورام. وقد يأتي الإشعاع منجهاز خارج الجسم (العلاج الإشعاعي الخارجي) أو يأتي من مادة مشعة موضوعة داخل الجسم بالقرب من الخلايا السرطانية (العلاج الإشعاعي الداخلي). يستخدم العلاج الإشعاعي مادة مشعة مثل الأجسام المضادة الجزيئية المشعة التي تسير في الدماء إلى داخل الأنسجة في مختلف أنحاء الجسم. ويطلق عليه أيضًا Irradiation and Radiotherapy.</p>', 58, 0, '2018-07-09 20:49:59', 0, 0),
('مشع (Radioactive):', '<p dir=\"rtl\">مطلق للإشعاع.</p>', 59, 0, '2018-07-09 20:49:59', 0, 0),
('أخصائي جراحات ترميمية (Reconstructive Surgeon)', '<p dir=\"rtl\">جرّاح يمكنه إجراء عملية جراحية لإعادة تشكيل أو بناء (إعادة ترميم) جزء من الجسم مثل ثدي سيدة خضعت لجراحة لعلاج سرطان الثدي.</p>', 60, 0, '2018-07-09 20:49:59', 0, 0),
('أخصائي تغذية معتمد (Registered dietitian)', '<p dir=\"rtl\">&nbsp;خبير رعاية صحية تلقى تدريبًا متخصصًا في استخدام الغذاء والتغذية للحفاظ على صحة الجسم. وإخصائي التغذية المعتمد قد يساعد الفريق الطبي على تحسين الصحة الغذائية للمريض.</p>', 61, 0, '2018-07-09 20:49:59', 0, 0),
('استئصال الثدي القطعي (Segmental Mastectomy)', '<p dir=\"rtl\">استئصال الورم السرطاني وكذلك بعض الأنسجة في الثدي الموجودة حول الورم والأنسجة المبطنة لعضلات الصدر الموجودة أسفل الورم. وعادة ما يتم استئصال بعض العقد اللمفية الموجودة تحت الإبط. ويطلق عليه أيضًا الاستئصال الجزئي للثدي Partial Mastectomy.</p>', 62, 0, '2018-07-09 20:49:59', 0, 0),
('الآثار الجانبية (Side effect)', '<p dir=\"rtl\">مشكلة تحدث حين يؤثر العلاج على الأنسجة أو الأعضاء السليمة. ومن بين الآثار الجانبية الشائعة لعلاج السرطان الإرهاق والألم والشعور بالغثيان والقيء وانخفاض عدد خلايا الدم وتساقط الشعر وقرح الفم.</p>', 63, 0, '2018-07-09 20:49:59', 0, 0),
('أخصائي اجتماعي (Social Work)', '<p dir=\"rtl\">متخصص مدرب على الحديث إلى المرضى وأسرهم عن الاحتياجات العاطفية أو البدنية وإيجاد الخدمات الداعمة لهم.</p>', 64, 0, '2018-07-09 20:49:59', 0, 0),
('عمليات جراحية (Surgery)', '<p dir=\"rtl\">إجراء يتم اتخاذه لاستئصال أو إصلاح جزء من الجسم أو لاكتشاف وجود المرض.</p>', 65, 0, '2018-07-09 20:49:59', 0, 0),
('تاموكسيفين (Tamoxifen)', '<p dir=\"rtl\">&nbsp;عقار يستخدم لعلاج أنواع معينة من سرطان الثدي في السيدات والرجال. كما أنه يستخدم أيضًا في الوقاية من سرطان الثدي في السيدات المصابات بسرطان بالغدد الثديية (خلايا غير سليمة في القنوات اللبنية بالثدي) وفي السيدات اللاتي يعانين من خطر الإصابة بسرطان الثدي بنسبة كبيرة. إنه يعوق تأثير هرمون الإستروجين في الثدي.</p>', 66, 0, '2018-07-09 20:49:59', 0, 0),
('العلاج الموجه (Targeted Therapy)', '<p dir=\"rtl\">نوع من العلاج تُستخدم فيه العقاقير أو مواد أخرى مثل الأجسام المضادة الجزيئية للتعرف على الخلايا السرطانية ومهاجمتها. وقد يكون للعلاج الموجه عدد من الآثار الجانبية أقل من تلك التي تحتويها أنواع أخرى من علاجات السرطان.</p>', 67, 0, '2018-07-09 20:49:59', 0, 0),
('نسيج (Tissue)', '<p dir=\"rtl\">مجموعة أو طبقة من الخلايا تعمل معًا لأداء وظيفة معينة.</p>', 68, 0, '2018-07-09 20:49:59', 0, 0),
('استئصال الثدي التام (Total Mastectomy)', '<p dir=\"rtl\">استئصال الثدي. ويطلق عليه استئصال بسيط للثدي.</p>', 69, 0, '2018-07-09 20:49:59', 0, 0),
('ورم (Tumor)', '<p dir=\"rtl\">كتلة غير طبيعية من الأنسجة التي تظهر حين تنقسم الخلايا أكثر مما ينبغي أو حين تموت هذه الخلايا حين لا ينبغي لها أن تموت. وقد تكون الأورام حميدة (غير سرطانية) أو خبيثة (سرطانية). ويطلق عليها أيضًا Neoplasm.</p>', 70, 0, '2018-07-09 20:49:59', 0, 0),
('الأشعة السينية (X-Ray)', '<p dir=\"rtl\">نوع من الإشعاع ذو طاقة، وفي الجرعات القليلة، تستخدم الأشعة السينية في تشخيص الأمراض من خلال تكوين صور للجزء الداخلي للجسم. وفي الجرعات الكبيرة، تُستخدم الأشعة السينية لعلاج السرطان.</p>', 71, 0, '2018-07-09 20:49:59', 0, 0);
